import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:firebase_admob/firebase_admob.dart';import "package:flutter_dibr_do_kospi/ad_manager.dart";import 'package:flutter/services.dart';import 'package:flutter_dibr_do_kospi/screen/info_screen.dart';import "package:flutter_dibr_do_kospi/model/model_marketHoliday.dart";import "package:flutter_dibr_do_kospi/screen/today_screen.dart";import "package:flutter_dibr_do_kospi/screen/history_screen.dart";import "package:flutter_dibr_do_kospi/widget/bottom.dart";import 'package:google_fonts/google_fonts.dart';import 'package:http/http.dart' as http;import 'dart:async';import 'dart:convert';class DibrContentPage extends StatefulWidget {  final List<String> holidayInfo;  DibrContentPage({this.holidayInfo});  @override  _DibrContentPageState createState() => _DibrContentPageState();}class _DibrContentPageState extends State<DibrContentPage> {  BannerAd _bannerAd;  List<String> _holidayInfo;  //Future<List<MarketHolidayInfo>> holidayInfo;  void _loadBannerAd() {    _bannerAd      ..load()      ..show(anchorType:  AnchorType.bottom, anchorOffset: 55);  }  @override  void initState() {    super.initState();    // TODO: Initialize _bannerAd    _bannerAd = BannerAd(      adUnitId: AdManager.bannerAdUnitId,      size: AdSize.banner,    );    // TODO: Load a Banner Ad    _loadBannerAd();    // TODO: Initialize _isRewardedAdReady    _loadRewardedAd();    _holidayInfo =  widget.holidayInfo;  }  @override  void dispose() {    // TODO: Dispose BannerAd object    _bannerAd?.dispose();    RewardedVideoAd.instance.listener = null;    super.dispose();  }  // TODO: Implement _loadRewardedAd()  void _loadRewardedAd() {    RewardedVideoAd.instance.load(      targetingInfo: MobileAdTargetingInfo(),      adUnitId: AdManager.rewardedAdUnitId,    );  }  @override  Widget build(BuildContext context) {    SystemChrome.setPreferredOrientations([DeviceOrientation.portraitUp]);//세로고정    return DefaultTabController(        length: 3,        child: Scaffold(          appBar: AppBar(            title:  Row(              mainAxisAlignment:              MainAxisAlignment.start,              children: <Widget>[                Padding(                  padding:                  const EdgeInsets                      .only(                      left: 3.0,                      right: 3.0),                  child: Align(                    alignment: Alignment                        .centerLeft,                    child: Image.asset(                        'images/logo_orange.png',                        height: 55,                        width: 100),                  ),                ),                Text(' Do Invest, Be Rich! ',                    style: GoogleFonts.mcLaren(                        fontWeight: FontWeight.bold,                        fontSize: 16,                        letterSpacing: 1.0,                        color: Colors.white)),              ],            ),            automaticallyImplyLeading: false,            backgroundColor: Colors.teal,            actions: <Widget>[              IconButton(                icon: Icon(Icons.share),                color: Colors.white,                onPressed: () {                },              ),            ],          ),          body: Padding(            padding: new EdgeInsets.only(bottom:70.0),            child: TabBarView(                physics:NeverScrollableScrollPhysics(),                children: <Widget>[                  DibrTodayPage( holidayInfo: _holidayInfo),                  DibrHistoryPage( holidayInfo: _holidayInfo),                  DibrInfoPage(),                ])),          bottomNavigationBar: Bottom(),        )    );  }}