hild: Scaffold(          appBar: AppBar(            title:  Text('[DIBR]Do Invest. Be Rich.', style: TextStyle(color: Colors.white),),            automaticallyImplyLeading: false,            backgroundColor: Colors.blueGrey,            actions: <Widget>[              IconButton(                icon: Icon(Icons.share),                color: Colors.white,                onPressed: () {                },              ),            ],          ),          body: TabBarView(              physics:NeverScrollableScrollPhysics(),              children: <Widget>[                DibrTodayPage(),                DibrHistoryPage(),                DibrInfoPage(),              ]),          bottomNavigationBar: Bottom(),        ));  }}